// import React from "react";
import React, { useState} from "react";
import { Button } from '@material-ui/core';
import { Dropdown } from 'react-bootstrap';

import API from "../../utils/API";

function NewJokeForm(props) {

  // const [jokes, setJokes] = useState([])
  const [newJoke, setNewJoke] = useState({})

  function handleInputChange(event) {
    const { name, value } = event.target;
    setNewJoke({...newJoke, [name]: value})
    };

  function handleFormSubmitNewJoke(event) {
      event.preventDefault();
      console.log("Hi from over here")  
      // if (newJoke.partOne) {
        API.saveJokes({
          userID: newJoke.userID,
          jokeID: newJoke.jokeID,
          joketype: newJoke.joketype,
          partOne: newJoke.partOne,
          partTwo: newJoke.partTwo
        })
        // .then(res => loadJokes())
        .catch(err => console.log(err));
      // }
    };

  return (
    <form>
      <div className="newJokeForm">
        <input
          className="textInput"
          onChange={handleInputChange}
          value={props.value}
          name="userID"
          type="text"
          className="form-control"
          placeholder="User ID"
          id="userID"
        />
        <input
          className="textInput"
          onChange={handleInputChange}
          value={props.value}
          name="jokeID"
          type="text"
          className="form-control"
          placeholder="Joke ID"
          id="jokeID"
        />

        {/*  many need to change back  */}

        {/* <input
          onChange={handleInputChange}
          value={props.value}
          name="joketype"
          type="text"
          className="form-control"
          placeholder="Joke type"
          id="joketype"
        /> */}

        <Dropdown className= "dropDown" >
        <Dropdown.Toggle className= "dropDownButton" variant="success" id="dropdown-basic">
        type of joke?
        </Dropdown.Toggle>

        <Dropdown.Menu>
        <Dropdown.Item className= "dropDownItem" href="#/action-1">Action</Dropdown.Item>
        <Dropdown.Item className= "dropDownItem" href="#/action-2">Another action</Dropdown.Item>
        <Dropdown.Item className= "dropDownItem" href="#/action-3">Something else</Dropdown.Item>
        </Dropdown.Menu>
        </Dropdown>

        <input
          className="textInput"
          onChange={handleInputChange}
          value={props.value}
          name="partOne"
          type="text"
          className="form-control"
          placeholder="Part 1"
          id="PartOne"
        />
        <input
          className="textInput"
          onChange={handleInputChange}
          value={props.value}
          name="partTwo"
          type="text"
          className="form-control"
          placeholder="Part 2"
          id="PartTwo"
        />
      </div>

      <Button 
        onClick={handleFormSubmitNewJoke} 
        className="btn btn-primary SaveNewJokeBtn">
        SAVE
      </Button>

    </form>
  );
}

export default NewJokeForm;
