// import React from "react";
import React, { useState, useEffect } from "react";
import CreateNewJokeBtn from "../components/CreateNewJokeBtn";
import HomePageBtn from "../components/HomePageBtn";
import LogOutBtn from "../components/LogOutBtn";
import OwnJokesBtn from "../components/OwnJokesBtn";
import SavedJokeCard from "../components/SavedJokeCard";
import Header from "../components/Header";
import Footer from "../components/Footer";
import Container from "../components/Container";
import Nav from "../components/Nav";

import API from "../utils/API";

function PersonalPage() {

  // Setting our component's initial state
  const [users, setUsers] = useState([])

  // Load all jokes and store them with setJokes
  useEffect(() => {
    console.log("hi")
    loadUsers()
  }, [])

  // Loads all jokes and sets them to jokes
  function loadUsers() {
    console.log("also hi")
    const user = localStorage.getItem("userInfo")
    const userInfo = JSON.parse(user)
    const userID = userInfo[0]._id
    console.log("also hi")
    API.getUsersById(userID)
      .then(res => 
        setUsers(res.data)
      )
      .catch(err => console.log(err));
  };

  return <div>
  <Header />
  <Nav>
  <CreateNewJokeBtn />
  <HomePageBtn />
  <LogOutBtn />
  <OwnJokesBtn />
  </Nav> 
  <Container>
  <SavedJokeCard users = {users} />
  </Container>
  <Footer />
  </div>       

}

export default PersonalPage;
