import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import SaveToLibraryBtn from "../SaveToLibraryBtn";
import { Button } from '@material-ui/core';

// import { useStoreContext } from "../../utils/GlobalState";
// import { SET_CURRENT_JOKE, ADD_FAVORITE } from "../../utils/actions";

import API from "../../utils/API";
import { List } from "../List";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

export default function JokeCard(props) {

// working on it
// const [state, dispatch] = useStoreContext();

const classes = useStyles();
console.log(props)

function deleteJoke(_id) {
  console.log(_id)
    API.deleteJokes(_id)
      .catch(err => console.log(err));
  }

  // i need user id here so i can push this into the aray for jokes under that user folder
function saveJoke(joke){
  console.log(joke)
  // API.saveJokeToUser({
  //   savedJokes: joke
  // })
  // .catch(err => console.log(err));
}

// working on it 
  // API.getJokes(props.match.params.id)
  //   .then(res => dispatch({ type: SET_CURRENT_JOKE, joke: res.data }))
  //   .catch(err => console.log(err));

  // const addFavorite = () => {
  //   dispatch({
  //     type: ADD_FAVORITE,
  //     // joke: state.currentJoke
  //     joke: "bob"
  //   });
  // };

  return (
    <div>
    {props.jokes.map(joke => (

    <List key={joke._id} >

    <Card className={classes.root} variant="outlined">

    <CardContent className="cardContent">

      <div>
        {/* added the state.  */}
              <div className="jokeCard">
              <div>{joke.joketype}</div>
              <div>{joke.partOne}</div>
              <div>{joke.partTwo}</div>
              </div> 
      </div>

    </CardContent>

      <Button 
        onClick={ () => deleteJoke(joke._id)} 
        className="btn btn-primary DeleteBtn">
        Delete
      </Button>

      {/* <SaveToLibraryBtn /> */}

      <Button 
        onClick={ () => saveJoke(joke)} 
        className="btn btn-primary saveBtn">
        save
      </Button>

    </Card>
    </List>
    ))} 
    </div>
  );
}
