// import React from "react";

import React, { useState} from "react";
import { Button } from '@material-ui/core';
import API from "../../utils/API";

const bcrypt = require("bcrypt");

// import API from "../../utils/API";


function NewUserForm(props) {

  const [newUser, setNewUser] = useState({})

  function handleInputChange(event) {
    const { name, value } = event.target;
    setNewUser({...newUser, [name]: value})
    };

  function handleFormSubmitNewUser(event) {
      event.preventDefault();
      console.log("Hi from over here we are entering user data")  

      const salt = bcrypt.genSalt();
      const hashPassword = bcrypt.hash(newUser.password, salt)


      // if (newUser.email && newUser.password) {
        API.saveUser({
          email: newUser.email,
          password: hashPassword,
        })
        // .then(res => loadJokes())
        .catch(err => console.log(err));
      // }
    };

  return (
    <form>
      <div className="newUserForm">
        <input
          onChange={handleInputChange}
          value={props.value}
          name="email"
          type="text"
          className="form-control"
          placeholder="email"
          id="email"
        />
        {/* <input
          onChange={props.handleInputChange}
          value={props.value}
          name="username"
          type="text"
          className="form-control"
          placeholder="username"
          id="username"
        /> */}
        <input
          onChange={handleInputChange}
          value={props.value}
          name="password"
          type="text"
          className="form-control"
          placeholder="password"
          id="password"
        />
      </div>

      <Button 
        onClick={handleFormSubmitNewUser} 
        className="btn btn-primary SaveNewUserBtn">
        SAVE
    </Button>

    </form>
  );
}

export default NewUserForm;
