import React, { useContext, useState } from "react";
import { Redirect } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";

// import React, { useState} from "react";
import { Button } from '@material-ui/core';

// needs working out 

function LoginForm(props) {

  const authContext = useContext(AuthContext);
  const [signInSuccess, setSignInSuccess] = useState();
  const [signInError, setSignInError] = useState();
  const [redirectOnSignIn, setRedirectOnSignIn] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

const submitCredentials = async (credentials) => {
    try {
      const url = "http://localhost:3001/api/auth";
      const fetchResponse = await fetch(url, {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
          "Content-Type": "application/json",
          // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
        body: JSON.stringify(credentials), // body data type must match "Content-Type" header
      });
      const data = await fetchResponse.json();
      authContext.setAuthState(data);
      setSignInSuccess(data.message);
      setSignInError(null);

      setTimeout(() => {
        setRedirectOnSignIn(true);
      }, 700);
    } catch (error) {
      setSignInError(error.message);
      setSignInSuccess(null);
    }
  };

  const handleLogIn = (event) => {
    event.preventDefault();

    console.log({ email, password })

    submitCredentials({ email, password });
  };

  return (

    <div>
    {redirectOnSignIn && <Redirect to="/home" />}
    {signInSuccess && <h1>success</h1>}
    {signInError && <h1>Error: {signInError} </h1>}

    <form>
      <div className="loginForm">
        <input
        //   onChange={handleInputChange}
          onChange={(e) => setEmail(e.target.value.trim())}
          value={props.value}
          name="email"
          type="text"
          className="form-control"
          placeholder="email"
          id="email"
        />
        <input
        //   onChange={handleInputChange}
          onChange={(e) => setPassword(e.target.value.trim())}
          value={props.value}
          name="password"
          type="text"
          className="form-control"
          placeholder="password"
          id="password"
        />
      </div>

      <Button 
        onClick={handleLogIn} 
        className="btn btn-primary LogInBtn">
        Log in
      </Button>

    </form>
    </div>
  );
}

export default LoginForm;
