// import React from "react";
import React, { useState } from "react";
import { Button } from '@material-ui/core';

import API from "../../utils/API";


function NewJokeForm(props) {

  const [formObject, setFormObject] = useState({})

  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
    };

  function handleFormSubmitNewJoke(event) {
      event.preventDefault();
      console.log("Hi from over here")
      if (formObject.joketype) {
        API.saveJokes({
          userID: "1",
          postID: 1,
          joketype: formObject.joketype,
          partOne: formObject.partOne,
          partTwo: formObject.partTwo
        })
          // .then(res => loadJokes())
          .catch(err => console.log(err));
      }
    };

  return (
    <form>
      <div className="newJokeForm">
        <input
          onChange={handleInputChange}
          value={props.value}
          name="title"
          type="text"
          className="form-control"
          placeholder="Title"
          id="title"
        />
        <input
          onChange={handleInputChange}
          value={props.value}
          name="part1"
          type="text"
          className="form-control"
          placeholder="Part 1"
          id="Part1"
        />
        <input
          onChange={handleInputChange}
          value={props.value}
          name="part2"
          type="text"
          className="form-control"
          placeholder="Part 2"
          id="Part2"
        />
      </div>

      <Button 
        onClick={handleFormSubmitNewJoke} 
        className="btn btn-primary SaveNewJokeBtn">
        SAVE
      </Button>

    </form>
  );
}

export default NewJokeForm;
