// import React from "react";
import React, { useState} from "react";
import { Button } from '@material-ui/core';
// import { Dropdown } from 'react-bootstrap';
import { DropDownList } from "@progress/kendo-react-dropdowns";

import API from "../../utils/API";

function NewJokeForm(props) {

  const joketypes = [
    {
      text: "one",
      id: 1,
    },
    {
      text: "two",
      id: 2,
    },
    {
      text: "three",
      id: 3,
    },
    {
      text: "four",
      id: 4,
    },
  ];

  // const [jokes, setJokes] = useState([])
  const [newJoke, setNewJoke] = useState({})

  function handleInputChange(event) {
    const { name, value } = event.target;
    setNewJoke({...newJoke, [name]: value})
    };

  function handleFormSubmitNewJoke(event) {
      event.preventDefault();
      console.log("Hi from over here")  
      // if (newJoke.partOne) {
        API.saveJokes({
          userID: newJoke.userID,
          jokeID: newJoke.jokeID,
          joketype: newJoke.joketype,
          partOne: newJoke.partOne,
          partTwo: newJoke.partTwo
        })
        // .then(res => loadJokes())
        .catch(err => console.log(err));
      // }
    };

  return (
    <form>
      <div className="newJokeForm">
        <input
          onChange={handleInputChange}
          value={props.value}
          name="userID"
          type="text"
          className="form-control"
          placeholder="User ID"
          id="userID"
        />
        <input
          onChange={handleInputChange}
          value={props.value}
          name="jokeID"
          type="text"
          className="form-control"
          placeholder="Joke ID"
          id="jokeID"
        />

        {/*  many need to change back  */}

        {/* <input
          onChange={handleInputChange}
          value={props.value}
          name="joketype"
          type="text"
          className="form-control"
          placeholder="Joke type"
          id="joketype"
        /> */}

        {/* <select className="dropdown" onChange={handleInputChange}> 
          <option className="dropdownItem" name="joketype" value="one">One</option>
          <option className="dropdownItem" name="joketype" value="two">Two</option>
        </select> */}

        <DropDownList
        data={joketypes}
        textField="text"
        dataItemKey="id"
        value={props.value}
        onChange={handleInputChange}
        />

        <input
          onChange={handleInputChange}
          value={props.value}
          name="partOne"
          type="text"
          className="form-control"
          placeholder="Part 1"
          id="PartOne"
        />
        <input
          onChange={handleInputChange}
          value={props.value}
          name="partTwo"
          type="text"
          className="form-control"
          placeholder="Part 2"
          id="PartTwo"
        />
      </div>

      <Button 
        onClick={handleFormSubmitNewJoke} 
        className="btn btn-primary SaveNewJokeBtn">
        SAVE
      </Button>

    </form>
  );
}

export default NewJokeForm;
