// import React from "react";
import React, { useState } from "react";
import { Button } from '@material-ui/core';

import API from "../../utils/API";

function NewJokeForm(props) {

  // const [jokes, setJokes] = useState([])
  const [newJoke, setNewJoke] = useState({})

  // Setting our component's initial state
  // const [jokes, setJokes] = useState([])

  // Load all jokes and store them with setJokes

  // useEffect(() => {
  //   console.log("hi")
  //   console.log(jokes)
  //   loadJokes()
  // }, [])

  // Loads all jokes and sets them to jokes

  // function loadJokes() {
  //   console.log("also hi")
  //   console.log(jokes)
  //   API.getJokes()
  //     .then(res => 
  //       setJokes(res.data)
  //     )
  //     .catch(err => console.log(err));
  // };

  // working this through

  function handleInputChange(event) {
    const { name, value } = event.target;
    setNewJoke({...newJoke, [name]: value})
    };

  function handleFormSubmitNewJoke(event) {
      event.preventDefault();
      console.log("Hi from over here")  
      if (newJoke.joketype && newJoke.partOne) {
        API.saveJokes({
          joketype : newJoke.joketype,
          partOne : newJoke.partOne,
          partTwo : newJoke.partTwo
        })
        // .then(res => loadJokes())
        .catch(err => console.log(err));
      }
    };

  return (
    <form>
      <div className="newJokeForm">
        <input
          onChange={handleInputChange}
          value={props.value}
          name="joketype"
          type="text"
          className="form-control"
          placeholder="Joke type"
          id="joketype"
        />
        <input
          onChange={handleInputChange}
          value={props.value}
          name="partOne"
          type="text"
          className="form-control"
          placeholder="Part 1"
          id="PartOne"
        />
        <input
          onChange={handleInputChange}
          value={props.value}
          name="partTwo"
          type="text"
          className="form-control"
          placeholder="Part 2"
          id="PartTwo"
        />
      </div>

      <Button 
        onClick={handleFormSubmitNewJoke} 
        className="btn btn-primary SaveNewJokeBtn">
        SAVE
      </Button>

    </form>
  );
}

export default NewJokeForm;
