      <div>
        {/* added the state.  */}
              <div className="jokeCard">
              <div>{joke.joketype}</div>
              <div>{joke.partOne}</div>
              <div>{joke.partTwo}</div>
              </div> 
      </div>

    </CardContent>

      <Button 
        onClick={ () => deleteJoke(joke._id)} 
        className="btn btn-primary DeleteBtn">
        Delete
      </Button>

      {/* <SaveToLibraryBtn /> */}

      <Button 
        onClick={ () => addFavorite(joke.id)} 
